import React from "react";

const Card = (props) => {
    const {name, email, id, image } = props;
    return (
        <div className="tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
            <img src={props.image} 
            width= "200px" height="200px" alt="California Flag" />
            <div>
                <h2>{props.name}</h2>
                <p>{props.email}</p>
            </div>
        </div>
    );
}

export default Card;
